E2Helper.Descriptions["tardisGet"] = "Returns exterior shell of a tardis when used on an occupant, the exterior itself, or the interior."
E2Helper.Descriptions["tardisDemat"] = "Initiates dematerialisation, takes vector pos and optional angle rot, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisPhase"] = "Toggles phase, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisFlightmode"] = "Toggles flightmode, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisLock"] = "Locks it, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisPhyslock"] = "Toggles physlock, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisPower"] = "Toggles power, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisIsomorph"] = "Toggles isomorphic security systems, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisLongflight"] = "Toggles long-flight, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisMaterialise"] = "Rematerialises, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisSelfrepair"] = "Toggles self-repair, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisTrack"] = "Sets tracking entity, takes entity ent, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisSpinmode"] = "Sets spinmode, takes number spinmode (-1=anti-clockwise|0=none|1=clockwise), returns spinmode as number."
E2Helper.Descriptions["tardisSetDestination"] = "Sets destination if in vortex, takes vector pos and optional angle rot, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisFastReturn"] = "Returns TARDIS to its previous takeoff position, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisHADS"] = "Toggles HADS, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisFastDemat"] = "Quickly dematerialises TARDIS, returns 1 if success or 0 if failure."
E2Helper.Descriptions["tardisMoving"] = "Returns 1 if teleporting or 0 if not teleporting."
E2Helper.Descriptions["tardisVisible"] = "Returns 1 if visible or 0 if invisible."
E2Helper.Descriptions["tardisFlying"] = "Returns 1 if flying or 0 if not flying."
E2Helper.Descriptions["tardisLocked"] = "Returns 1 if locked or 0 if not locked."
E2Helper.Descriptions["tardisPhyslocked"] = "Returns 1 if physlocked or 0 if not physlocked."
E2Helper.Descriptions["tardisPowered"] = "Returns 1 if powered or 0 if not powered."
E2Helper.Descriptions["tardisIsomorphic"] = "Returns 1 if isomorphic security systems are enabled or 0 if isomorphic security systems are disabled."
E2Helper.Descriptions["tardisLongflighted"] = "Returns 1 if long-flight is enabled or 0 if long-flight is disabled."
E2Helper.Descriptions["tardisSelfrepairing"] = "Returns 1 if self-repairing or 0 if not self-repairing."
E2Helper.Descriptions["tardisHealth"] = "Returns tardis health."
E2Helper.Descriptions["tardisLastPos"] = "Returns the vector of where the TARDIS last dematerialised from."
E2Helper.Descriptions["tardisLastAng"] = "Returns the angle of where the TARDIS last dematerialised from."
E2Helper.Descriptions["tardisTracking"] = "Returns the entity the TARDIS is tracking."
E2Helper.Descriptions["tardisInVortex"] = "Returns 1 if in vortex, or 0 if not."
E2Helper.Descriptions["tardisIsHADS"] = "Returns 1 if HADS is enabled, or 0 if not."
E2Helper.Descriptions["tardisPilot"] = "Returns the pilot if valid."
E2Helper.Descriptions["tardisDoor"] = "Toggles the TARDIS doors. Returns a 1 if successful, or a 0 if not."
E2Helper.Descriptions["tardisDoorOpen"] = "Returns 1 if the TARDIS doors are open, or 0 if not."